/* 定义图片容器的样式 */
figure.wp-block-image.size-large {
    position: relative;
    overflow: hidden;
    display: inline-block;
    transition: transform 0.3s ease; /* 添加变换过渡效果 */
}

/* 定义图片的样式 */
figure.wp-block-image.size-large img {
    width: 100%;
    transition: all 0.3s ease;
}

/* 使用伪元素在第一张图片中间插入"ASSESSMENT TOOL"文字 */
figure.wp-block-image.size-large:nth-of-type(1)::before {
  /*  content: "ASSESSMENT TOOL";*/
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: transparent;
    font-size: 20px;
    transition: all 0.3s ease;
    z-index: 1;
}

/* 使用伪元素在第二张图片中间插入"LIBRARY RESOURCE"文字 */
figure.wp-block-image.size-large:nth-of-type(2)::before {
  /*   content: "LIBRARY RESOURCE";*/
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: transparent;
    font-size: 20px;
    transition: all 0.3s ease;
    z-index: 2;
}

/* 使用伪元素在第三张图片中间插入"AI Prediction"文字 */
figure.wp-block-image.size-large:nth-of-type(3)::before {
  /*   content: "AI Prediction";*/
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: transparent;
    font-size: 20px;
    transition: all 0.3s ease;
    z-index: 2;
}

/* 当鼠标悬停在图片容器上时的样式 */
figure.wp-block-image.size-large:hover {
    transform: scale(1.02); /* 图片微微放大 */
}

figure.wp-block-image.size-large:hover img {
    filter: brightness(0.5); /* 使图片变暗 */
}

figure.wp-block-image.size-large:hover::before {
    color: white; /* 文字颜色变为白色 */
    transform: translate(-50%, -50%) scale(1.1); /* 文字微微放大 */
}

