    <script>
        $(document).ready(function() {
            

            const MAX_CHILDHOOD_SCORE = 68;
            const MAX_SCHOOL_EXPERIENCE_SCORE = 16;
            const MAX_RELATIONSHIP_BEHAVIOUR = 20;
            const MAX_WORKPLACE_ENVIRONMENT = 15;
            const MAX_EQUAL_PAY_AND_COMPENSATION = 10;
            const MAX_PROMOTIONS_AND_OPPORTUNITIES = 15;
            const MAX_WORKPLACE_POLICIES_SCORE = 10;

            let retrievedChildhood = parseInt(localStorage.getItem('Childhood'), 10) || 0;
            let retrievedSchool_Experience = parseInt(localStorage.getItem('School Experience'), 10) || 0;
            let retrievedRelationship_Behaviour = parseInt(localStorage.getItem('Relationship Behaviour'), 10) || 0;
            
            let retrievedWorkplace_Environment = parseInt(localStorage.getItem('Workplace Environment'), 10) || 0;
            let retrievedEqual_Pay_and_Compensation = parseInt(localStorage.getItem('Equal Pay and Compensation'), 10) || 0;
            let retrievedPromotions_and_Opportunities = parseInt(localStorage.getItem('Promotions and Opportunities'), 10) || 0;
            let retrievedWorkplace_Policies = parseInt(localStorage.getItem('Workplace Policies'), 10) || 0;



            function determineLabel(score) {
                if (score >= 0 && score < 14) {
                    return { text: "Excellent", class: "label-excellent" };
                } else if (score >= 14 && score < 28) {
                    return { text: "Good", class: "label-good" };
                } else if (score >= 28 && score < 42) {
                    return { text: "Average", class: "label-average" };
                } else {
                    return { text: "Below Average", class: "label-below-average" };
                }
            }
            
            function determineLabel_Sc(score) {
                if (score >= 0 && score < 4) {
                    return { text: "Excellent", class: "label-excellent" };
                } else if (score >= 4 && score < 8) {
                    return { text: "Good", class: "label-good" };
                } else if (score >= 8 && score < 12) {
                    return { text: "Average", class: "label-average" };
                } else {
                    return { text: "Below Average", class: "label-below-average" };
                }
            }
            
            function determineLabel_Wo(score) {
                if (score >= 0 && score < 3) {
                    return { text: "Excellent", class: "label-excellent" };
                } else if (score >= 3 && score < 7) {
                    return { text: "Good", class: "label-good" };
                } else if (score >= 7 && score < 10) {
                    return { text: "Average", class: "label-average" };
                } else {
                    return { text: "Below Average", class: "label-below-average" };
                }
            }
            
            function determineLabel_Eq(score) {
                if (score >= 0 && score < 3) {
                    return { text: "Excellent", class: "label-excellent" };
                } else if (score >= 3 && score < 5) {
                    return { text: "Good", class: "label-good" };
                } else if (score >= 5 && score < 7) {
                    return { text: "Average", class: "label-average" };
                } else {
                    return { text: "Below Average", class: "label-below-average" };
                }
            }
            
            function determineLabel_Pr(score) {
                if (score >= 0 && score < 3) {
                    return { text: "Excellent", class: "label-excellent" };
                } else if (score >= 3 && score < 7) {
                    return { text: "Good", class: "label-good" };
                } else if (score >= 7 && score < 10) {
                    return { text: "Average", class: "label-average" };
                } else {
                    return { text: "Below Average", class: "label-below-average" };
                }
            }

            function setSkillLabel(skillName, retrievedScore) {
                const label = determineLabel(retrievedScore);
                $(`.skill:has(.skill-name:contains('${skillName}')) .label`).text(label.text).addClass(label.class);
            }
            
            function setSkillLabel_Sc(skillName, retrievedScore) {
                const label = determineLabel_Sc(retrievedScore);
                $(`.skill:has(.skill-name:contains('${skillName}')) .label`).text(label.text).addClass(label.class);
            }
            
            function setSkillLabel_Wo(skillName, retrievedScore) {
                const label = determineLabel_Sc(retrievedScore);
                $(`.skill:has(.skill-name:contains('${skillName}')) .label`).text(label.text).addClass(label.class);
            }
            
            function setSkillLabel_Eq(skillName, retrievedScore) {
                const label = determineLabel_Eq(retrievedScore);
                $(`.skill:has(.skill-name:contains('${skillName}')) .label`).text(label.text).addClass(label.class);
            }
            
            function setSkillLabel_Pr(skillName, retrievedScore) {
                const label = determineLabel_Pr(retrievedScore);
                $(`.skill:has(.skill-name:contains('${skillName}')) .label`).text(label.text).addClass(label.class);
            }
            
            setSkillLabel('Childhood', retrievedChildhood);
            setSkillLabel_Sc('School Experience', retrievedSchool_Experience);
            setSkillLabel_Sc('Relationship Behaviour', retrievedRelationship_Behaviour);
            setSkillLabel_Wo('Workplace Environment', retrievedWorkplace_Environment);
            setSkillLabel_Eq('Equal Pay and Compensation', retrievedEqual_Pay_and_Compensation);
            setSkillLabel_Pr('Promotions and Opportunities', retrievedPromotions_and_Opportunities);
            setSkillLabel_Eq('Workplace Policies', retrievedWorkplace_Policies);

        });
    </script>
