                return { text: "Good", class: "label-good" };
            } else if (score >= 7 && score < 10) {
                return { text: "Average", class: "label-average" };
            } else {
                return { text: "Below Average", class: "label-below-average" };
            }
        }
    
        function determineLabelCategory(scorePercentage, labelName) {
            let score;
            switch(labelName) {
                case "School Experience":
                    score = (scorePercentage * MAX_SCHOOL_EXPERIENCE_SCORE) / 100;
                    return determineLabel_Sc(score).text;
                case "Workplace Environment":
                    score = (scorePercentage * MAX_WORKPLACE_ENVIRONMENT) / 100;
                    return determineLabel_Wo(score).text;
                case "Equal Pay and Compensation":
                    score = (scorePercentage * MAX_EQUAL_PAY_AND_COMPENSATION) / 100;
                    return determineLabel_Eq(score).text;
                case "Promotions and Opportunities":
                    score = (scorePercentage * MAX_PROMOTIONS_AND_OPPORTUNITIES) / 100;
                    return determineLabel_Pr(score).text;
                default:
                    score = (scorePercentage * MAX_CHILDHOOD_SCORE) / 100;
                    return determineLabel(score).text;
            }
        }
    
        let dataPoints = [
            determineScorePercentage(retrievedChildhood, MAX_CHILDHOOD_SCORE),
            determineScorePercentage(retrievedSchool_Experience, MAX_SCHOOL_EXPERIENCE_SCORE),
            determineScorePercentage(retrievedRelationship_Behaviour, MAX_RELATIONSHIP_BEHAVIOUR),
            determineScorePercentage(retrievedWorkplace_Environment, MAX_WORKPLACE_ENVIRONMENT),
            determineScorePercentage(retrievedEqual_Pay_and_Compensation, MAX_EQUAL_PAY_AND_COMPENSATION),
            determineScorePercentage(retrievedPromotions_and_Opportunities, MAX_PROMOTIONS_AND_OPPORTUNITIES),
            determineScorePercentage(retrievedWorkplace_Policies, MAX_WORKPLACE_POLICIES_SCORE)
        ];
    
        var ctx = document.getElementById('radarChart').getContext('2d');
        var radarChart = new Chart(ctx, {
            type: 'radar',
            data: {
                labels: [
                    'C', 
                    'SE', 
                    'RB', 
                    'WE', 
                    'EC', 
                    'PO', 
                    'WP'
                ],
                datasets: [{
                    label: 'Score Percentage',
                    data: dataPoints,
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
                borderColor: 'rgba(255, 99, 132, 1)',
                borderWidth: 2
            }]
        },
        options: {
            scales: {
