                    },
                    suggestedMin: 0,
                    suggestedMax: 100,
                    pointLabels: {
                            font: {
                              size: 13,
                              weight: 'bold',
                             
                              
                              
                            }
                    }
                }
            },
            animation: {
                duration: 3000, // 动画的持续时间，单位为毫秒
                easing: 'easeInOutQuart', // 动画的缓动函数，这里选择的是 "easeInOutQuart"
                onComplete: function() { 
                    // 这个函数在动画完成后会被调用，您可以在这里添加任何需要的代码
                }
            },
            plugins: {
                legend: {
                    display: false
                },
                tooltip: {
                    callbacks: {
                        label: function(context) {
                            let label = '';
                            let score = context.raw;
        
                            // 判断该点对应哪个标签，并应用相应的评判标准函数
                            switch (context.label) {
                                case 'C':
                                    label = determineLabel(score).text;
                                    break;
                                case 'SE':
                                    label = determineLabel_Sc(score).text;
                                    break;
                                case 'RB':
                                    label = determineLabel(score).text;
                                    break;
