<script>
$(document).ready(function() {
    const MAX_SCORES = {
        "Childhood": 68,
        "School Experience": 16,
        "Relationship Behaviour": 20,
        "Workplace Environment": 15,
        "Equal Pay and Compensation": 10,
        "Promotions and Opportunities": 15,
        "Workplace Policies": 10
    };

    function retrieveScore(skillName) {
        
        return parseInt(localStorage.getItem(skillName), 10) || 0;
    }

    function determineLabel(skillName, score) {
        
        
        let thresholds = {
            "Childhood": { excellent: 14, good: 28, average: 42},
            "School Experience": { excellent: 4, good: 8, average: 12 },
            "Relationship Behaviour": { excellent: 4, good: 8, average: 12 },
            "Workplace Environment": { excellent: 3, good: 7, average: 10 },
            "Equal Pay and Compensation": { excellent: 3, good: 5, average: 7 },
            "Promotions and Opportunities": { excellent: 3, good: 7, average: 10 },
            "Workplace Policies": { excellent: 3, good: 5, average: 7 }
        };
    
        let currentThresholds = thresholds[skillName];
    
        if (score < currentThresholds.excellent) {
            return { text: "Excellent", class: "label-excellent" };
        } else if (score < currentThresholds.good && score >= currentThresholds.excellent) {
            return { text: "Good", class: "label-good" };
        } else if (score < currentThresholds.average && score >= currentThresholds.good) {
            return { text: "Average", class: "label-average" };
        } else {
            return { text: "Below Average", class: "label-below-average" };
        }
    }
    

    function getFeedback(skillType, label) {
        const generalAdvice = {
            "excellent": "We know you are very good at this part, which shows that you are not gender biased in this part. Continue to maintain this open and restrictive mindset.",
            "good": "We understand that you are good in this section, which means you may have some biases in certain situations. Try to understand and challenge your inner beliefs.",
            "average": "You are average in this section, which means you may show bias in some situations. It is recommended to study and reflect more deeply.",
            "below average": "You are below average in this section, and you may show bias in many situations. The important thing is to start learning, challenge your perspectives, and seek understanding."
        };


        return `${generalAdvice[label]} `;
    }

    const feedbackMapping = {
        "Childhood": "childhood-suggestion",
        "School Experience": "school-suggestion",
        "Relationship Behaviour": "relationship-suggestion",
        "Workplace Environment": "workplace-env-suggestion",
        "Equal Pay and Compensation": "equal-pay-suggestion",
        "Promotions and Opportunities": "promotions-suggestion",
        "Workplace Policies": "policies-suggestion"
    };

    function setSkillLabel(skillName) {
        
        const retrievedScore = retrieveScore(skillName);
        
        const labelData = determineLabel(skillName, retrievedScore);
        const skillElement = $(`.skill[data-skill="${skillName.toLowerCase().replace(/ /g, '-')}"] .label`);
        
        skillElement.text(labelData.text);
        skillElement.addClass(labelData.class);
        
        // 获取相应的反馈部分的ID
        const feedbackSection = $(`#${feedbackMapping[skillName]}`);
        const feedbackText = getFeedback(skillName.toLowerCase().replace(/ /g, '-').replace(/&/g, 'and'), labelData.text.toLowerCase());
        const feedbackItem = $('<li>').text(feedbackText);
        feedbackSection.append(feedbackItem);
    }

    Object.keys(MAX_SCORES).forEach(skill => {
        setSkillLabel(skill);
        
    });
});


</script>
