<script>
jQuery(document).ready(function($) {
    $('.case-link').click(function(e) {
        e.preventDefault();
        var caseId = $(this).data('case');
        
        if ($('#case-' + caseId).is(':visible')) {
            // If the clicked case study is already visible, hide it
            $('#case-' + caseId).hide();
        } else {
            // Hide all other case studies
            $('.case-study').hide();
            $('.reveal-button').text('Reveal Correction');
            $('.corrected-text, .explanation').hide();

            // Show the clicked case study
            $('#case-' + caseId).show();
        }
    });

    $('.reveal-button').click(function() {
        var caseId = $(this).data('case');
        if ($('#case-' + caseId + ' .corrected-text').is(':visible')) {
            $(this).text('Reveal Correction');
            $('#case-' + caseId + ' .corrected-text, #case-' + caseId + ' .explanation').hide();
        } else {
            $(this).text('Hide Correction');
            $('#case-' + caseId + ' .corrected-text, #case-' + caseId + ' .explanation').show();
        }
    });
});
</script>







